import http from 'k6/http';
import { check, sleep } from 'k6';
import { Counter, Trend } from 'k6/metrics';

// Custom metrics for database operations
let dbErrors = new Counter('database_errors');
let dbConnectionTime = new Trend('db_connection_time');
let dbQueryTime = new Trend('db_query_time');

export let options = {
  stages: [
    { duration: '30s', target: ${ramp_up_users} },
    { duration: '${duration_seconds}s', target: ${virtual_users} },
    { duration: '30s', target: 0 },
  ],
  thresholds: {
    'http_req_duration': ['p(95)<${response_time_threshold}'],
    'http_req_failed': ['rate<0.05'],
    'database_errors': ['count<10'],
  },
};

export default function() {
  // Test database read operations
  let readResponse = http.get('${target_url}/api/v1/users?limit=10');
  let readSuccess = check(readResponse, {
    'read operation succeeds': (r) => r.status === 200,
    'read response time OK': (r) => r.timings.duration < ${response_time_threshold},
    'read returns data': (r) => JSON.parse(r.body).length > 0,
  });

  if (readSuccess) {
    dbQueryTime.add(readResponse.timings.duration);
  } else {
    dbErrors.add(1);
  }

  // Test database write operations (lighter load)
  if (__VU % 5 === 0) {  // Only 20% of users do writes
    let writePayload = JSON.stringify({
      name: `TestUser${__VU}`,
      timestamp: new Date().toISOString()
    });

    let writeResponse = http.post('${target_url}/api/v1/users', writePayload, {
      headers: { 'Content-Type': 'application/json' },
    });

    check(writeResponse, {
      'write operation succeeds': (r) => r.status >= 200 && r.status < 300,
      'write response time OK': (r) => r.timings.duration < ${response_time_threshold} * 2,
    }) || dbErrors.add(1);
  }

  sleep(1 + Math.random() * 2);  // Random sleep 1-3s
}
